import React, { useState } from "react";
import { registerPatient } from "../api";

const PatientForm = ({ onUpdate }) => {
  const [name, setName] = useState("");
  const [age, setAge] = useState(30);
  const [problem, setProblem] = useState("");
  const [urgency, setUrgency] = useState("Low");
  const [resourceChoice, setResourceChoice] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = { name, age, problem, urgency };
    if (urgency === "Medium") {
      data.resourceChoice = resourceChoice;
    }
    const res = await registerPatient(data);
    alert(res.message);
    onUpdate();
  };

  return (
    <form onSubmit={handleSubmit} className="p-6 bg-white rounded-lg shadow-md space-y-4">
      <h2 className="text-xl font-bold">Register New Patient</h2>
      <div>
        <label className="block font-medium">Name:</label>
        <input
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="border rounded w-full p-2"
        />
      </div>
      <div>
        <label className="block font-medium">Age:</label>
        <input
          type="number"
          value={age}
          onChange={(e) => setAge(Number(e.target.value))}
          className="border rounded w-full p-2"
        />
      </div>
      <div>
        <label className="block font-medium">Problem:</label>
        <textarea
          value={problem}
          onChange={(e) => setProblem(e.target.value)}
          className="border rounded w-full p-2"
        />
      </div>
      <div>
        <label className="block font-medium">Urgency:</label>
        <select
          value={urgency}
          onChange={(e) => setUrgency(e.target.value)}
          className="border rounded w-full p-2"
        >
          <option>Low</option>
          <option>Medium</option>
          <option>High</option>
        </select>
      </div>
      {urgency === "Medium" && (
        <div>
          <p className="font-medium">Allocate to:</p>
          <div className="space-x-4">
            {['ICU', 'General', 'Ventilator'].map((choice) => (
              <label key={choice} className="inline-flex items-center">
                <input
                  type="radio"
                  name="resourceChoice"
                  value={choice}
                  onChange={(e) => setResourceChoice(e.target.value)}
                  className="mr-2"
                />
                {choice}
              </label>
            ))}
          </div>
        </div>
      )}
      <button
        type="submit"
        className="mt-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
      >
        Submit
      </button>
    </form>
  );
};

export default PatientForm;
