// src/components/PatientForm.jsx
import React, { useState } from "react";
import { registerPatient } from "/Users/bluemishu/Downloads/Project_solvit2/frontend/src/api";

const PatientForm = ({ onUpdate }) => {
  const [name, setName] = useState("");
  const [age, setAge] = useState(30);
  const [problem, setProblem] = useState("");
  const [urgency, setUrgency] = useState("Low");
  const [resourceChoice, setResourceChoice] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = { name, age, problem, urgency };
    if (urgency === "Medium") {
      data.resourceChoice = resourceChoice;
    }
    const res = await registerPatient(data);
    alert(res.message);
    onUpdate();
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Register New Patient</h2>
      <label>
        Name:
        <input value={name} onChange={(e) => setName(e.target.value)} />
      </label>
      <br />
      <label>
        Age:
        <input
          type="number"
          value={age}
          onChange={(e) => setAge(Number(e.target.value))}
        />
      </label>
      <br />
      <label>
        Problem:
        <textarea value={problem} onChange={(e) => setProblem(e.target.value)} />
      </label>
      <br />
      <label>
        Urgency:
        <select value={urgency} onChange={(e) => setUrgency(e.target.value)}>
          <option>Low</option>
          <option>Medium</option>
          <option>High</option>
        </select>
      </label>
      <br />
      {urgency === "Medium" && (
        <div>
          <p>Allocate to:</p>
          <label>
            <input
              type="radio"
              name="resourceChoice"
              value="ICU"
              onChange={(e) => setResourceChoice(e.target.value)}
            />
            ICU
          </label>
          <label>
            <input
              type="radio"
              name="resourceChoice"
              value="General"
              onChange={(e) => setResourceChoice(e.target.value)}
            />
            General
          </label>
          <label>
            <input
              type="radio"
              name="resourceChoice"
              value="Ventilator"
              onChange={(e) => setResourceChoice(e.target.value)}
            />
            Ventilator
          </label>
        </div>
      )}
      <br />
      <button type="submit">Submit</button>
    </form>
  );
};

export default PatientForm;
