import React, { useState, useEffect } from "react";
import { fetchQueue, completePatient } from "../api";

const PatientQueue = () => {
  const [queue, setQueue] = useState([]);

  const loadQueue = async () => {
    const data = await fetchQueue();
    setQueue(data);
  };

  useEffect(() => {
    loadQueue();
  }, []);

  const handleComplete = async (firestoreId) => {
    if (firestoreId) {
      await completePatient(firestoreId);
      loadQueue();
    }
  };

  return (
    <div className="p-6 bg-white rounded-lg shadow-md">
      <h2 className="text-xl font-bold mb-4">Patient Queue</h2>
      {queue && queue.length > 0 ? (
        <div className="space-y-3">
          {queue.map((patient, index) => (
            <div
              key={patient.firestore_id || index}
              className="border-b border-gray-300 pb-2"
            >
              <p>
                {index + 1}. {patient.name} (Urgency: {patient.urgency})
              </p>
              <button
                onClick={() => handleComplete(patient.firestore_id)}
                className="mt-1 px-3 py-1 bg-green-500 text-white rounded hover:bg-green-600"
              >
                Mark as Completed
              </button>
            </div>
          ))}
        </div>
      ) : (
        <p>No patients in the queue.</p>
      )}
    </div>
  );
};

export default PatientQueue;
