// src/components/Dashboard.jsx
import React, { useState, useEffect } from "react";
import { fetchResources, fetchQueue, resetSystem } from "/Users/bluemishu/Downloads/Project_solvit2/frontend/src/api.js";

const Dashboard = () => {
  const [resources, setResources] = useState({});
  const [queue, setQueue] = useState([]);

  useEffect(() => {
    updateResources();
    updateQueue();
  }, []);

  const updateResources = async () => {
    const data = await fetchResources();
    setResources(data);
  };

  const updateQueue = async () => {
    const data = await fetchQueue();
    setQueue(data);
  };

  const handleReset = async () => {
    await resetSystem();
    updateResources();
    updateQueue();
  };

  return (
    <div>
      <h2>Hospital Resource Status</h2>
      <div>
        <p>ICU Beds: {resources.icu}</p>
        <p>General Beds: {resources.general}</p>
        <p>MRI Usage: {resources.mri}</p>
        <p>Ventilators: {resources.ventilators}</p>
      </div>
      <button onClick={handleReset}>Reset System</button>
      <h2>Patient Queue</h2>
      {queue.map((patient, index) => (
        <div key={patient.firestore_id || index}>
          <p>
            {index + 1}. {patient.name} (Urgency: {patient.urgency})
          </p>
        </div>
      ))}
    </div>
  );
};

export default Dashboard;
