import React, { useState, useEffect } from "react";
import { fetchResources, fetchQueue, resetSystem } from "../api";

const Dashboard = () => {
  const [resources, setResources] = useState({});
  const [queue, setQueue] = useState([]);

  useEffect(() => {
    updateResources();
    updateQueue();
  }, []);

  const updateResources = async () => {
    const data = await fetchResources();
    setResources(data);
  };

  const updateQueue = async () => {
    const data = await fetchQueue();
    setQueue(data);
  };

  const handleReset = async () => {
    await resetSystem();
    updateResources();
    updateQueue();
  };

  return (
    <div className="p-6 bg-white rounded-lg shadow-md">
      <h2 className="text-2xl font-bold mb-4">Hospital Resource Status</h2>
      <div className="grid grid-cols-2 gap-4">
        <p>ICU Beds: <span className="font-semibold">{resources.icu}</span></p>
        <p>General Beds: <span className="font-semibold">{resources.general}</span></p>
        <p>MRI Usage: <span className="font-semibold">{resources.mri}</span></p>
        <p>Ventilators: <span className="font-semibold">{resources.ventilators}</span></p>
      </div>
      <button
        onClick={handleReset}
        className="mt-4 px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
      >
        Reset System
      </button>
      <h2 className="text-xl font-bold mt-6">Patient Queue</h2>
      <div className="space-y-2 mt-2">
        {queue.map((patient, index) => (
          <div key={patient.firestore_id || index} className="bg-gray-100 p-3 rounded">
            <p>
              {index + 1}. {patient.name} (Urgency: {patient.urgency})
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
