// src/components/ForecastCharts.jsx
import React, { useState, useEffect } from "react";
import { Line } from "react-chartjs-2";
import {
    fetchOccupancyForecast,
    fetchBedsForecast,
  } from "../api";
// ✅ Required Chart.js registrations
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// ✅ Register chart components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);



const ForecastCharts = () => {
  const [occupancyData, setOccupancyData] = useState(null);
  const [bedsData, setBedsData] = useState(null);

  useEffect(() => {
    fetchOccupancyForecast().then((data) => {
      const labels = Object.keys(data);
      const values = Object.values(data);
      setOccupancyData({
        labels: labels,
        datasets: [
          {
            label: "Bed Occupancy Forecast (7 Days)",
            data: values,
            fill: false,
            borderColor: "rgba(75, 192, 192, 1)",
            tension: 0.3,
          },
        ],
      });
    });

    fetchBedsForecast().then((data) => {
      const labels = data.map((item) => item.date);
      const values = data.map((item) => item.predicted_beds);
      setBedsData({
        labels: labels,
        datasets: [
          {
            label: "Predicted Beds Forecast (30 Days)",
            data: values,
            fill: false,
            borderColor: "rgba(153, 102, 255, 1)",
            tension: 0.3,
          },
        ],
      });
    });
  }, []);

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        display: true,
        position: "top",
      },
    },
    scales: {
      x: {
        type: "category", // <-- important
        title: {
          display: true,
          text: "Date",
        },
      },
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: "Beds",
        },
      },
    },
  };

  return (
    <div>
      <h2>Forecast Charts</h2>

      {occupancyData && (
        <div>
          <h3>Bed Occupancy Forecast (7 Days)</h3>
          <Line data={occupancyData} options={chartOptions} />
        </div>
      )}

      {bedsData && (
        <div>
          <h3>Predicted Beds Forecast (Next 30 Days)</h3>
          <Line data={bedsData} options={chartOptions} />
        </div>
      )}
    </div>
  );
};

export default ForecastCharts;
